package com.syntax.class09;

public class Pattern2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i=1; i<=5; i++) {
			for (int j=5; j>=1; j--) {
				System.out.print(j+" ");
			}
			System.out.println();
		}
System.out.println("=========pattern Number 2==========");
		for (int i=5; i>=1; i--) {
			for (int j=1; j<=5; j++) {
				System.out.print(i+" ");
			}
			System.out.println();
	}
	}
}
